TEST(evmmax, bn254_add)
{
    std::set<bytes> seen_inputs;

    for (const auto& t : TEST_CASES)
    {
        auto input = from_hex(t.input_hex).value();
        ASSERT_LE(input.size(), 128);
        input.resize(128);
        auto expected_output = from_hex(t.expected_output_hex).value();
        ASSERT_LE(expected_output.size(), 64);
        expected_output.resize(64);

        // Ensure no duplicate inputs.
        ASSERT_TRUE(seen_inputs.insert(input).second) << t.input_hex;

        const auto p = AffinePoint::from_bytes(std::span<const uint8_t, 64>{&input[0], 64});
        const auto q = AffinePoint::from_bytes(std::span<const uint8_t, 64>{&input[64], 64});
        const auto e =
            AffinePoint::from_bytes(std::span<const uint8_t, 64>{&expected_output[0], 64});

        ASSERT_TRUE(p.has_value());
        ASSERT_TRUE(validate(*p));
        ASSERT_TRUE(q.has_value());
        ASSERT_TRUE(validate(*q));
        ASSERT_TRUE(e.has_value());
        ASSERT_TRUE(validate(*e));

        const auto r = add_affine(*p, *q);
        EXPECT_EQ(r, e);
    }
}
